mapper.select:
SELECT s.fin_prdt_cd, s.kor_co_nm, s.fin_prdt_nm, s.join_way, s.prdt_div, s.selectCount, o.intr_rate_type_nm, o.save_trm, o.intr_rate, o.intr_rate2, o.rsrv_type FROM savingsDeposit s LEFT JOIN savingsDeposit_options o ON s.fin_prdt_cd = o.fin_prdt_cd where o.save_trm = "12"
AND s.prdt_div = #{prdtDiv}
ORDER BY s.selectCount DESC LIMIT 0, 3

SELECT s.fin_prdt_cd, s.kor_co_nm, s.fin_prdt_nm, s.join_way, s.prdt_div, s.selectCount, o.intr_rate_type_nm, o.save_trm, o.intr_rate, o.intr_rate2, o.rsrv_type FROM savingsDeposit s LEFT JOIN savingsDeposit_options o ON s.fin_prdt_cd = o.fin_prdt_cd
s.prdt_div = #{prdtDiv}
o.save_trm = '12'
ORDER BY s.fin_prdt_cd LIMIT #{limit} OFFSET #{offset}

mapper.insert:
INSERT INTO savingsDeposit (
    fin_prdt_cd, kor_co_nm, fin_prdt_nm, join_way, spcl_cnd,
    join_member, etc_note, max_limit, prdt_div
) VALUES (
             #{savingsDeposit.finPrdtCd}, #{savingsDeposit.korCoNm}, #{savingsDeposit.finPrdtNm},
             #{savingsDeposit.joinWay}, #{savingsDeposit.spclCnd}, #{savingsDeposit.joinMember},
             #{savingsDeposit.etcNote}, #{savingsDeposit.maxLimit}, #{savingsDeposit.prdtDiv}
         )
